import 'package:flutter/material.dart';
import 'package:share/share.dart';
import 'package:rsa_encrypt/rsa_encrypt.dart';
import 'package:pointycastle/api.dart' as crypto;

class User {
  bool isMem;
  bool isAdmin;
  String id;
  crypto.AsymmetricKeyPair keyPair;
  String private = '';
  String public = '';

  User({@required this.id, @required this.isMem, @required this.isAdmin}){
    this.id = id;
    this.isAdmin = isAdmin;
    this.isMem = isMem;
    RsaKeyHelper keyHelp = new RsaKeyHelper();
    keyPair = new crypto.AsymmetricKeyPair(keyHelp.parsePublicKeyFromPem(public), keyHelp.parsePrivateKeyFromPem(private));
  }
}